mapper.update:

update t_txcheck



        isok=#{isok}


        excute=#{excute}


where id=#{id}

mapper.select:
select u.rname,lg.bankcard,lg.banktype,lg.money,tx.isok,tx.excute,tx.date_time from t_txcheck as tx ,t_logtx as lg,t_huser as u
where u.huid=tx.huid and tx.txid=lg.id
limit #{pager.beginIndex}, #{pager.pageSize}
